                case 'PUT':
                case 'PATCH':
                case 'POST':
                    $body_type = $multipart ? 'multipart' : 'form_params';

                    if (isset($this->requestHeader['Content-Type'])
                        && $this->requestHeader['Content-Type'] === 'application/json'
                    ) {
                        $body_type = 'json';
