class Picture {
    public $compressLimit;
    public $sizeLimit;

    public function __construct(int $compressLimit, int $sizeLimit) {
        $this->compressLimit = $compressLimit;
        $this->sizeLimit = $sizeLimit;
    }

    public function downloadImage(string $url): string {
        $ch = curl_init($url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        $response = curl_exec($ch);
        curl_close($ch);

        if ($response === false) {
            return '';
        }

        $image = imagecreatefromstring($response);
        if (!$image) {
            return '';
        }

        $imageInfo = getimagesize($response);
        $width = $imageInfo[0];
        $height = $imageInfo[1];

        if ($width > $this->sizeLimit || $height > $this->sizeLimit) {
            $newWidth = $this->sizeLimit;
            $newHeight = $this->sizeLimit;

            if ($width > $height) {
                $newHeight = ($height / $width) * $this->sizeLimit;
            } else {
                $newWidth = ($width / $height) * $this->sizeLimit;
            }

            $resizedImage = imagecreatetruecolor($newWidth, $newHeight);
            imagecopyresampled($resizedImage, $image, 0, 0, 0, 0, $newWidth, $newHeight, $width, $height);
            imagedestroy($image);
            $image = $resizedImage;
        }

        $originalSize = strlen($response);
        $processedImage = $response;

        if ($originalSize > $this->compressLimit) {
            $imageType = image_type_to_mime_type($image);
            if ($imageType === 'image/jpeg') {
                ob_start();
                imagejpeg($image, null, 75);
                $processedImage = ob_get_clean();
            } else {
                ob_start();
                imagepng($image, null, 9);
                $processedImage = ob_get_clean();
            }
        }

        return $processedImage;
    }
}

