class Http {
    public static function fsockopenDownload(string $url, array $conf = []): mixed {
        $parsedUrl = parse_url($url);
        if (!isset($parsedUrl['scheme']) || !isset($parsedUrl['host'])) {
            return '';
        }

        $defaults = [
            'limit' => 0,
            'post' => null,
            'cookie' => '',
            'ip' => '',
            'timeout' => 30,
            'block' => true,
        ];
        $config = array_merge($defaults, $conf);

        $headers = [];
        if ($config['cookie']) {
            $headers[] = 'Cookie: ' . $config['cookie'];
        }
        $headers[] = 'User-Agent: Mozilla/5.0';
        $headers[] = 'Accept: */*';

        $method = 'GET';
        $body = '';
        if ($config['post'] !== null) {
            if (is_array($config['post'])) {
                $body = http_build_query($config['post']);
            } else {
                $body = $config['post'];
            }
            $method = 'POST';
            $headers[] = 'Content-Type: application/x-www-form-urlencoded';
            $headers[] = 'Content-Length: ' . strlen($body);
        }

        $requestLine = "$method {$parsedUrl['path']} HTTP/1.1";
        $request = $requestLine . "\r\n" . implode("\r\n", $headers) . "\r\n\r\n";

        $port = isset($parsedUrl['port']) ? $parsedUrl['port'] : 80;
        $host = $parsedUrl['host'];
        $ip = $config['ip'] ?: $host;

        $socket = fsockopen($ip, $port, $errno, $errstr, $config['timeout']);
        if (!$socket) {
            return '';
        }

        stream_set_blocking($socket, $config['block'] ? 1 : 0);

        fwrite($socket, $request);

        $response = '';
        while (!feof($socket)) {
            $line = fgets($socket);
            if ($line === false) {
                break;
            }
            $response .= $line;
            if (substr($line, -2) === "\r\n") {
                break;
            }
        }

        $remaining = $config['limit'] > 0 ? $config['limit'] : PHP_INT_MAX;
        $bodyData = '';
        while ($remaining > 0 && !feof($socket)) {
            $chunk = fread($socket, min(8192, $remaining));
            if ($chunk === false) {
                break;
            }
            $bodyData .= $chunk;
            $remaining -= strlen($chunk);
        }

        fclose($socket);
        return $bodyData;
    }
}

