    $file = $params['file'];
    $url = parse_url($file);
    $target_host = $url['host'];
    $target_path = $url['path'] ?? '/';
    $target_port = isset($url['port']) ? $url['port'] : 80;

    $proxy_host = $params['proxy_host'] ?? null;
    $proxy_port = $params['proxy_port'] ?? null;
    $timeout = $params['timeout'] ?? 30;
    $assign = $params['assign'] ?? null;

    $connect_host = $proxy_host ?: $target_host;
    $connect_port = $proxy_port ?: $target_port;

    $fp = fsockopen($connect_host, $connect_port, $errno, $errstr, $timeout);
    if (!$fp) {
        return '';
    }

    $request = "GET $target_path HTTP/1.1\r\n";
    $request .= "Host: $target_host\r\n";
    $request .= "Connection: close\r\n\r\n";

    fwrite($fp, $request);

    $response = '';
