        $line = fgets($fp);
        if ($line === false) break;
        $response .= $line;
        if (substr($line, 0, 1) === "\r" || substr($line, 0, 1) === "\n") {
            break;
        }
    }

    $body = '';
    while (!feof($fp)) {
        $body .= fgets($fp);
    }

    fclose($fp);

    if ($assign) {
        $template->$assign = $body;
    } else {
