class URLChecker {
    const POLL_INTERVAL_MS = 5000;
    const CONNECT_TIMEOUT_MS = 3000;

    private function getHTTPResponseCode(string $url): ?int {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_CONNECTTIMEOUT_MS, self::CONNECT_TIMEOUT_MS);
        $response = curl_exec($ch);
        if ($response === false) {
            curl_close($ch);
            return null;
        }
        $http_code = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);
        return $http_code;
    }

    public function waitUntilAvailable(int $timeout_in_ms, string $url): self {
        $start_time = microtime(true) * 1000;
        while (true) {
            $response_code = $this->getHTTPResponseCode($url);
            if ($response_code === 200) {
                return $this;
            }
            $elapsed = (microtime(true) * 1000) - $start_time;
            if ($elapsed > $timeout_in_ms) {
                throw new TimeoutException("Timeout exceeded while waiting for URL to be available");
            }
            usleep($this::POLL_INTERVAL_MS * 1000);
        }
    }

    public function waitUntilUnavailable(int $timeout_in_ms, string $url): self {
        $start_time = microtime(true) * 1000;
        while (true) {
            $response_code = $this->getHTTPResponseCode($url);
            if ($response_code !== 200) {
                return $this;
            }
            $elapsed = (microtime(true) * 1000) - $start_time;
            if ($elapsed > $timeout_in_ms) {
                throw new TimeoutException("Timeout exceeded while waiting for URL to be unavailable");
            }
            usleep($this::POLL_INTERVAL_MS * 1000);
        }
    }
}

