        $params = [];
        if (!empty($outBizBo)) {
            $params['out_biz_no'] = $outBizBo;
        } else {
            $params['order_id'] = $orderId;
        }
        if (empty($params)) {
            throw new Exception('No valid order ID or out_biz_no provided');
        }
        $sign = $this->generateSign($params);
        $params['sign'] = $sign;
        $url = 'https://openapi.alipay.com/gateway.do';
        $response = $this->curlPost($url, $params, [
            CURLOPT_SSL_VERIFYPEER => true,
            CURLOPT_SSL_VERIFYHOST => 2,
        ]);
        return json_decode($response, true);
    }

    public function generateSign(array $params, string $signType = 'RSA'): string {
        $content = $this->getSignContent($params);
        return $this->sign($content, $signType);
    }

    private function sign(string $data, string $signType = 'RSA'): string {
        $algorithm = $signType === 'RSA' ? OPENSSL_ALGO_SHA256 : OPENSSL_ALGO_SHA1;
        $signature = '';
        $success = openssl_sign($data, $signature, $this->rsaPrivateKey, $algorithm);
        if (!$success) {
            throw new Exception('Failed to sign data');
        }
        return base64_encode($signature);
    }

    private function checkEmpty(mixed $value): bool {
        return is_null($value) || empty($value);
    }

    private function getSignContent(array $params): string {
        $targetCharset = $this->charset;
        foreach ($params as &$value) {
            $value = $this->characet($value, $targetCharset);
        }
        ksort($params);
        return http_build_query($params);
    }

    private function characet(string $data, string $targetCharset): string {
        return mb_convert_encoding($data, $targetCharset, 'UTF-8');
    }

    private function curlPost(string $url, mixed $postData, array $options = []): string {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_POST, true);
        curl_setopt($ch, CURLOPT_POSTFIELDS, $postData);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        foreach ($options as $key => $value) {
            curl_setopt($ch, $key, $value);
        }
        $response = curl_exec($ch);
        if ($response === false) {
            throw new Exception('cURL error: ' . curl_error($ch));
        }
        curl_close($ch);
        return $response;
    }
}

