class Image {
    const BASE_URL = 'https://via.placeholder.com/';
    const FORMAT_JPG = 'jpg';
    const FORMAT_JPEG = 'jpeg';
    const FORMAT_PNG = 'png';

    protected $categories = ['animals', 'nature', 'people', 'technology', 'food', 'sports', 'abstract'];

    public function imageUrl(
        int $width,
        int $height,
        string $category = null,
        bool $randomize = true,
        string $word = null,
        bool $gray = false,
        string $format = 'png'
    ): string {
        $url = self::BASE_URL . $width . 'x' . $height . '.' . $format;

        if ($category) {
            $url .= '?category=' . urlencode($category);
        }

        if ($randomize) {
            $url .= '&randomize=true';
        }

        if ($word) {
            $url .= '&text=' . urlencode($word);
        }

        if ($gray) {
            $url .= '&gray=true';
        }

        return $url;
    }

    private function generateFilename(
        int $width,
        int $height,
        string $category = null,
        bool $randomize = true,
        string $word = null,
        bool $gray = false,
        string $format = 'png'
    ): string {
        $base = 'img_' . $width . 'x' . $height . '.' . $format;

        if ($category) {
            $base .= '_' . $category;
        }

        if ($word) {
            $base .= '_' . $word;
        }

        if ($randomize) {
            $base .= '-' . bin2hex(random_bytes(16));
        }

        return $base;
    }

    public function image(
        string $dir = null,
        int $width = 640,
        int $height = 480,
        string $category = null,
        bool $fullPath = true,
        bool $randomize = true,
        string $word = null,
        bool $gray = false,
        string $format = 'png'
    ): string|false {
        $url = $this->imageUrl($width, $height, $category, $randomize, $word, $gray, $format);
        if (empty($url)) {
            return false;
        }

        $filePath = $dir ?: 'images/';
        $filename = $this->generateFilename($width, $height, $category, $randomize, $word, $gray, $format);
        $fullPath = $fullPath ? $filePath . $filename : $filename;

        if (!is_dir($filePath)) {
            if (!mkdir($filePath, 0777, true)) {
                return false;
            }
        }

        $data = false;
        if (function_exists('curl_init')) {
            $ch = curl_init($url);
            curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
            $data = curl_exec($ch);
            curl_close($ch);
        } else {
            $data = file_get_contents($url);
        }

        if ($data === false) {
            return false;
        }

        if (file_put_contents($fullPath, $data) === false) {
            return false;
        }

        return $fullPath;
    }
}

