            'out_trade_no' => $this->outTradeNo,
            'total_amount' => $this->totalFee,
            'subject' => $this->orderName,
        ];
        $sign = $this->generateSign($params, 'RSA2');
        $params['sign'] = $sign;
        $params['sign_type'] = 'RSA2';
        $response = $this->curlPost('https://openapi.alipay.com/gateway.do', $params);
        return json_decode($response, true);
    }

    private function curlPost(string $url, mixed $postData, array $options = []): string {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_POST, 1);
        curl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($postData));
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
        $result = curl_exec($ch);
        if ($result === false) {
            throw new Exception('cURL error: ' . curl_error($ch));
        }
        curl_close($ch);
        return $result;
    }

    private function generateSign(array $params, string $signType): string {
        ksort($params);
        $stringToSign = http_build_query($params, '', '&', PHP_QUERY_RFC3986);
        $privateKey = openssl_pkey_get_private($this->rsaPrivateKey);
        if (!$privateKey) {
            throw new Exception('Failed to load private key');
        }
        $signature = '';
        openssl_sign($stringToSign, $signature, $privateKey, $signType);
        return base64_encode($signature);
    }
