        $oauth = [
            'oauth_consumer_key' => $this->consumer_key,
            'oauth_token' => $this->oauth_access_token,
            'oauth_signature_method' => 'HMAC-SHA1',
            'oauth_version' => '1.0',
            'oauth_nonce' => self::generateNonce(),
            'oauth_timestamp' => time()
        ];

        $baseString = $this->buildBaseString($url, $requestMethod, $oauth);
        $signature = hash_hmac('sha1', $baseString, $this->consumer_secret . '&' . $this->oauth_access_token_secret);
        $oauth['oauth_signature'] = $signature;

        $this->oauth = $oauth;
        return $this;
    }

    public function performRequest(bool $return = true) {
        $ch = curl_init();
        $url = $this->url;

        if ($this->getfield) {
            $url .= '?' . $this->getfield;
        }

        $headers = [];
        if (isset($this->oauth)) {
            $headers[] = $this->buildAuthorizationHeader($this->oauth);
        }

        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);

        if ($this->postfields) {
            curl_setopt($ch, CURLOPT_POST, true);
            curl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($this->postfields));
        }

        if ($return) {
            $response = curl_exec($ch);
            curl_close($ch);
            return $response;
        } else {
            curl_close($ch);
        }
    }

    private function buildBaseString(string $baseURI, string $method, array $params) {
        $params = $this->normalizeParams($params);
        $baseString = $method . '&' . rawurlencode($baseURI) . '&' . rawurlencode(http_build_query($params));
        return $baseString;
    }

    private function normalizeParams(array $params) {
        uksort($params, function($a, $b) {
            return strcmp($a, $b);
        });
        foreach ($params as $k => $v) {
            $params[$k] = rawurlencode($k) . '=' . rawurlencode($v);
        }
        return $params;
    }

    private function buildAuthorizationHeader(array $oauth) {
        $header = 'Authorization: OAuth ';
        $parts = [];
        foreach ($oauth as $key => $value) {
            $parts[] = $key . '="' . rawurlencode($value) . '"';
        }
        $header .= implode(', ', $parts);
        return $header;
    }

    private static function generateNonce() {
        return md5(uniqid(mt_rand(), true));
    }
}

