class Client {
    public static function userAgent() {
        $sdk = 'SDK/1.0';
        $system = php_uname('s');
        $phpVersion = phpversion();
        return "$sdk $system PHP/$phpVersion";
    }

    public static function parseHeaders($raw) {
        $headers = [];
        $lines = explode("\r\n", $raw);
        foreach ($lines as $line) {
            if (empty($line)) continue;
            list($name, $value) = explode(':', $line, 2);
            $name = self::ucwordsHyphen($name);
            $headers[$name] = $value;
        }
        return $headers;
    }

    public static function escapeQuotes($str) {
        return str_replace(['"', "'"], ['\"', '\''], $str);
    }

    public static function ucwordsHyphen($str) {
        $parts = explode('-', $str);
        $capitalized = array_map('ucfirst', $parts);
        return implode('-', $capitalized);
    }

    public static function sendRequest($url, $method, $body = null, $headers = []) {
        $ch = curl_init();

        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);
        curl_setopt($ch, CURLOPT_TIMEOUT, 30);
        curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false);
        curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, false);

        if ($method === 'POST' || $method === 'PUT') {
            curl_setopt($ch, CURLOPT_POST, true);
            curl_setopt($ch, CURLOPT_POSTFIELDS, $body);
        } elseif ($method === 'GET') {
            curl_setopt($ch, CURLOPT_POST, false);
        } elseif ($method === 'DELETE') {
            curl_setopt($ch, CURLOPT_CUSTOMREQUEST, 'DELETE');
        }

        curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);

        $userAgent = self::userAgent();
        curl_setopt($ch, CURLOPT_USERAGENT, $userAgent);

        $startTime = microtime(true);
        $response = curl_exec($ch);
        $endTime = microtime(true);
        $timing = [
            'start' => $startTime,
            'end' => $endTime,
            'duration' => $endTime - $startTime
        ];

        if ($response === false) {
            $error = curl_error($ch);
            $httpCode = 500;
            $headers = [];
            $body = $error;
        } else {
            $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
            $headersSize = curl_getinfo($ch, CURLINFO_HEADER_SIZE);
            $headersRaw = substr($response, 0, $headersSize);
            $body = substr($response, $headersSize);
            $headers = self::parseHeaders($headersRaw);
        }

        curl_close($ch);

        return new Response($httpCode, $headers, $body, $timing);
    }

    public static function get($url, $headers = []) {
        return self::sendRequest($url, 'GET', null, $headers);
    }

    public static function delete($url, $headers = []) {
        return self::sendRequest($url, 'DELETE', null, $headers);
    }

    public static function post($url, $body, $headers = []) {
        return self::sendRequest($url, 'POST', $body, $headers);
    }

    public static function put($url, $body, $headers = []) {
        return self::sendRequest($url, 'PUT', $body, $headers);
    }

    public static function multipartPost(
        string $url,
        array $fields,
        string $name,
        string $fileName,
        string $fileBody,
        string $mimeType = null,
        array $headers = []
    ) {
        $boundary = '---------------------------' . bin2hex(random_bytes(16));
        $bodyParts = [];

        foreach ($fields as $key => $value) {
            $bodyParts[] = "--$boundary\r\nContent-Disposition: form-data; name=\"$key\"\r\n\r\n" . self::escapeQuotes($value) . "\r\n";
        }

        $bodyParts[] = "--$boundary\r\nContent-Disposition: form-data; name=\"$name\"; filename=\"$fileName\"\r\nContent-Type: $mimeType\r\n\r\n" . self::escapeQuotes($fileBody) . "\r\n";
        $bodyParts[] = "--$boundary--\r\n";

        $body = implode('', $bodyParts);
        $headers['Content-Type'] = 'multipart/form-data; boundary=' . $boundary;

        return self::sendRequest($url, 'POST', $body, $headers);
    }
}

