        foreach ($curl_options as $key => $value) {
            curl_setopt($ch, $key, $value);
        }

        $response = curl_exec($ch);

        if ($response === false) {
            $this->error = curl_error($ch);
            $this->success = false;
        } else {
            $headers_part = substr($response, 0, curl_getinfo($ch, CURLINFO_HEADER_SIZE));
            $body_part = substr($response, curl_getinfo($ch, CURLINFO_HEADER_SIZE));

            $headers_array = http_parse_headers($headers_part);
            $this->headers = $headers_array;

            $status_line = explode(' ', $headers_part, 2);
            $this->status_code = (int)$status_line[1];

            $this->url = curl_getinfo($ch, CURLINFO_EFFECTIVE_URL);
            $this->redirects = curl_getinfo($ch, CURLINFO_REDIRECT_COUNT);
            $this->body = $body_part;

            $this->success = ($this->status_code >= 200 && $this->status_code < 300);
        }

        curl_close($ch);
    }

