        $this->document = $document;
    }

    public function getDocument() {
        return $this->document;
    }

    public function loadFromUrl(string $url, ?Options $options = null, ?ClientInterface $client = null, ?RequestInterface $request = null): Dom {
        $client = $client ?? new Client();
        $request = $request ?? new Request('GET', $url);

        try {
            $response = $client->sendRequest($request);
        } catch (\Exception $e) {
            throw new \Exception("Guzzle request failed: " . $e->getMessage());
        }

        if ($response->getStatusCode() !== 200) {
            throw new \Exception("HTTP request failed with status code {$response->getStatusCode()}");
        }

        $body = (string) $response->getBody();

        $dom = new DOMDocument();
        try {
            if ($options) {
                $flags = $options->getLoadFlags();
                $dom->loadHTML($body, $flags);
            } else {
                $dom->loadHTML($body);
            }
        } catch (\Exception $e) {
            throw new \Exception("Failed to load HTML into DOM: " . $e->getMessage());
        }

        return new self($dom);
    }
}

