        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        $headersSize = curl_getinfo($ch, CURLINFO_HEADER_SIZE);
        $headers = substr($response, 0, $headersSize);
        $body = substr($response, $headersSize);
        $error = null;

        if ($response === false) {
            $error = new Qiniu_Error(curl_errno($ch), curl_error($ch));
        } else {
            if ($httpCode >= 200 && $httpCode < 300) {
                $headersArray = $this->parseHeaders($headers);
                $responseObj = new Qiniu_Response($httpCode, $body, $headersArray);
                return [$responseObj, $error];
            } else {
