                $contentType = is_string($opts['data']) && strpos($opts['data'], '{') === 0 ? 'json' : 'multipart/form-data';
                curl_setopt($ch, CURLOPT_POST, true);
                curl_setopt($ch, CURLOPT_POSTFIELDS, $opts['data']);
                curl_setopt($ch, CURLOPT_HTTPHEADER[] , $this->contentTypes[$contentType]);
