        if ($fp === false) return 0;
        $written = fwrite($fp, $d);
        if ($written === false) return 0;
        self::$curlDataWritten += $written;
        return $written;
    }

    public function getURL($url, $tempfile) {
        self::$tempfile = $tempfile;
        $this->curlFH = fopen($tempfile, 'wb');
        if (!$this->curlFH) {
            $this->lastURLError = 'Could not open file';
            return false;
        }

        if (!function_exists('curl_init')) {
            $content = file_get_contents($url);
            if ($content === false) {
                $this->lastURLError = 'Could not fetch via file_get_contents';
                fclose($this->curlFH);
                return false;
            }
