            'sl' => urlencode($source),
            'tl' => urlencode($target),
            'q' => urlencode($text)
        );

        $max = 9000;
        if(strlen($fields['q']) >= $max)
            throw new \Exception("Maximum number of characters exceeded: ".strlen($fields['q'])."/$max");

        $fields_string = "";
        foreach ($fields as $key => $value) {
            $fields_string .= $key . '=' . $value . '&';
