class Picture {
    private $compressLimit;
    private $sizeLimit;

    public function __construct($compressLimit, $sizeLimit) {
        $this->compressLimit = $compressLimit;
        $this->sizeLimit = $sizeLimit;
    }

    public function downloadImage($url) {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);
        curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false);

        $response = curl_exec($ch);
        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);

        if ($httpCode != 200) {
            return false;
        }

        $imageSize = strlen($response);
        if ($imageSize > $this->compressLimit) {
            $response = $this->compressImage($response);
        }

        return $response;
    }

    private function compressImage($imageData) {
        $imageResource = imagecreatefromstring($imageData);
        if (!$imageResource) {
            return false;
        }

        $width = imagesx($imageResource);
        $height = imagesy($imageResource);

        if ($width > $this->sizeLimit || $height > $this->sizeLimit) {
            $newWidth = $this->sizeLimit;
            $newHeight = $this->sizeLimit;
            $newImage = imagecreatetruecolor($newWidth, $newHeight);
            imagecopyresampled($newImage, $imageResource, 0, 0, 0, 0, $newWidth, $newHeight, $width, $height);
            $imageResource = $newImage;
        }

        ob_start();
        imagepng($imageResource, null, 8); // Compression level 8
        $compressedData = ob_get_clean();
        imagedestroy($imageResource);

        return $compressedData;
    }
}

