function _dfsockopen($url, $limit = 0, $post = '', $cookie = '', $bysocket = FALSE, $ip = '', $timeout = 15, $block = TRUE, $encodetype = 'URLENCODE', $allowcurl = TRUE, $position = 0, $files = array()) {
    $matches = parse_url($url);
    $scheme = $matches['scheme'];
    $host = $matches['host'];
    $path = $matches['path'] ? $matches['path'].($matches['query'] ? '?'.$matches['query'] : '') : '/';
    $port = !empty($matches['port']) ? $matches['port'] : ($scheme == 'http' ? '80' : '');

    if($post) {
        if(!is_array($post)) {
            parse_str($post, $post);
        }
        _format_postkey($post, $postnew);
        $post = $postnew;
    }

    if(function_exists('curl_init') && function_exists('curl_exec') && $allowcurl) {
        $ch = curl_init();
        $httpheader = array();
        if($ip) {
            $httpheader[] = "Host: ".$host;
        }
        curl_setopt($ch, CURLOPT_URL, $scheme.'://'.($ip ? $ip : $host).($port ? ':'.$port : '').$path);
        curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false);
        curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, false);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_HEADER, 1);
        if($post) {
            curl_setopt($ch, CURLOPT_POST, 1);
            if($encodetype == 'URLENCODE') {
                curl_setopt($ch, CURLOPT_POSTFIELDS, $post);
            } else {
                foreach($post as $k => $v) {
                    if(isset($files[$k])) {
                        $post[$k] = '@'.$files[$k];
                    }
                }
                curl_setopt($ch, CURLOPT_POSTFIELDS, $post);
            }
        }
        if($cookie) {
            curl_setopt($ch, CURLOPT_COOKIE, $cookie);
        }
        curl_setopt($ch, CURLOPT_CONNECTTIMEOUT, $timeout);
        $data = curl_exec($ch);
        $status = curl_getinfo($ch);
        curl_close($ch);
        if($status['http_code'] != 200) {
            return;
        } else {
            $GLOBALS['filesockheader'] = substr($data, 0, $status['header_size']);
            $data = substr($data, $status['header_size']);
            return !$limit ? $data : substr($data, 0, $limit);
        }
    }

    $boundary = $encodetype == 'URLENCODE' ? '' : random(40);
    if($post) {
        if($encodetype == 'URLENCODE') {
            $data = http_build_query($post);
        } else {
            $data = '';
            foreach($post as $k => $v) {
                $data .= "--$boundary\r\n";
                $data .= 'Content-Disposition: form-data; name="'.$k.'"'.(isset($files[$k]) ? '; filename="'.basename($files[$k]).'"; Content-Type: application/octet-stream' : '')."\r\n\r\n";
                $data .= $v."\r\n";
            }
            $data .= "--$boundary\r\n";
        }
        $out = "POST $path HTTP/1.0\r\n";
        $header = "Content-Type: ".($encodetype == 'URLENCODE' ? "application/x-www-form-urlencoded" : "multipart/form-data; boundary=$boundary")."\r\n";
        $header .= 'Content-Length: '.strlen($data)."\r\n";
    } else {
        $out = "GET $path HTTP/1.0\r\n";
        $header = "";
    }

    $header .= "Host: $host:$port\r\n";
    $header .= "Connection: Close\r\n";
    $header .= "Cookie: $cookie\r\n\r\n";
    $out .= $header;
    if($post) $out .= $data;

    if(!$fp = @fsocketopen(($ip ? $ip : $host), $port, $errno, $errstr, $timeout)) {
        return '';
    }

    stream_set_blocking($fp, $block);
    stream_set_timeout($fp, $timeout);
    @fwrite($fp, $out);

    $headers = '';
    while (!feof($fp)) {
        $header = @fgets($fp);
        $headers .= $header;
        if($header && ($header == "\r\n" || $header == "\n")) {
            break;
        }
    }
    $GLOBALS['filesockheader'] = $headers;

    $return = $limit ? stream_get_contents($fp, $limit) : stream_get_contents($fp);
    @fclose($fp);
    return $return;
}

function _format_postkey($post, &$result, $key = '') {
    foreach($post as $k => $v) {
        $_k = $key ? $key.'['.$k.']' : $k;
        if(is_array($v)) {
            _format_postkey($v, $result, $_k);
        } else {
            $result[$_k] = $v;
        }
    }
}

use PhpAmqpLib\Connection\AMQPStreamConnection;
use PhpAmqpLib\Message\AMQPMessage;

require __DIR__ . '/../vendor/autoload.php';
require __DIR__ . '/../tests/config.php';

class RabbitMQConsumer
{
    protected $msgCount = 0;
    protected $startTime = null;
    protected $conn;
    protected $ch;
    protected $exchange = 'bench_exchange';
    protected $queue = 'bench_queue';
    protected $consumer_tag = '';

    public function __construct()
    {
        $this->conn = new AMQPStreamConnection(HOST, PORT, USER, PASS, VHOST);
        $this->ch = $this->conn->channel();
        $this->ch->queue_declare($this->queue, false, false, false, false);
        $this->ch->exchange_declare($this->exchange, 'direct', false, false, false);
        $this->ch->queue_bind($this->queue, $this->exchange);
    }

    public function process_message($msg)
    {
        if ($this->startTime === null) {
            $this->startTime = microtime(true);
        }

        if ($msg->body == 'quit') {
            echo sprintf("Pid: %s, Count: %s, Time: %.4f\n", getmypid(), $this->msgCount, microtime(true) - $this->startTime);
            $this->shutdown();
            die;
        }
        $this->msgCount++;
    }

    public function startConsuming()
    {
        $this->ch->basic_consume($this->queue, '', false, true, false, false, array($this, 'process_message'));
        while ($this->ch->is_consuming()) {
            $this->ch->wait();
        }
    }

    protected function shutdown()
    {
        $this->ch->close();
        $this->conn->close();
    }
}

class CI_Session_redis_driver extends CI_Session_driver implements SessionHandlerInterface {

    protected $_redis;
    protected $_key_prefix = 'ci_session:';
    protected $_lock_key;
    protected $_key_exists = FALSE;

    public function __construct(&$params)
    {
        parent::__construct($params);

        if (empty($this->_config['save_path']))
        {
            log_message('error', 'Session: No Redis save path configured.');
        }
        elseif (preg_match('#(?:tcp://)?([^:?]+)(?:\:(\d+))?(\?.+)?#', $this->_config['save_path'], $matches))
        {
            isset($matches[3]) OR $matches[3] = '';
            $this->_config['save_path'] = array(
                'host' => $matches[1],
                'port' => empty($matches[2]) ? NULL : $matches[2],
                'password' => preg_match('#auth=([^\s&]+)#', $matches[3], $match) ? $match[1] : NULL,
                'database' => preg_match('#database=(\d+)#', $matches[3], $match) ? (int) $match[1] : NULL,
                'timeout' => preg_match('#timeout=(\d+\.\d+)#', $matches[3], $match) ? (float) $match[1] : NULL
            );

            preg_match('#prefix=([^\s&]+)#', $matches[3], $match) && $this->_key_prefix = $match[1];
        }
        else
        {
            log_message('error', 'Session: Invalid Redis save path format: '.$this->_config['save_path']);
        }

        if ($this->_config['match_ip'] === TRUE)
        {
            $this->_key_prefix .= $_SERVER['REMOTE_ADDR'].':';
        }
    }

    public function open($save_path, $name)
    {
        if (empty($this->_config['save_path']))
        {
            return $this->_fail();
        }

        $redis = new Redis();
        if ( ! $redis->connect($this->_config['save_path']['host'], $this->_config['save_path']['port'], $this->_config['save_path']['timeout']))
        {
            log_message('error', 'Session: Unable to connect to Redis with the configured settings.');
        }
        elseif (isset($this->_config['save_path']['password']) && ! $redis->auth($this->_config['save_path']['password']))
        {
            log_message('error', 'Session: Unable to authenticate to Redis instance.');
        }
        elseif (isset($this->_config['save_path']['database']) && ! $redis->select($this->_config['save_path']['database']))
        {
            log_message('error', 'Session: Unable to select Redis database with index '.$this->_config['save_path']['database']);
        }
        else
        {
            $this->_redis = $redis;
            return $this->_success;
        }

        return $this->_fail();
    }

    public function read($session_id)
    {
        if (isset($this->_redis) && $this->_get_lock($session_id))
        {
            $this->_session_id = $session_id;

            $session_data = $this->_redis->get($this->_key_prefix.$session_id);

            is_string($session_data)
                ? $this->_key_exists = TRUE
                : $session_data = '';

            $this->_fingerprint = md5($session_data);
            return $session_data;
        }

        return $this->_fail();
    }

    public function write($session_id, $session_data)
    {
        if ( ! isset($this->_redis, $this->_lock_key))
        {
            return $this->_fail();
        }
        elseif ($session_id !== $this->_session_id)
        {
            if ( ! $this->_release_lock() OR ! $this->_get_lock($session_id))
            {
                return $this->_fail();
            }

            $this->_key_exists = FALSE;
            $this->_session_id = $session_id;
        }

        $this->_redis->setTimeout($this->_lock_key, 300);
        if ($this->_fingerprint !== ($fingerprint = md5($session_data)) OR $this->_key_exists === FALSE)
        {
            if ($this->_redis->set($this->_key_prefix.$session_id, $session_data, $this->_config['expiration']))
            {
                $this->_fingerprint = $fingerprint;
                $this->_key_exists = TRUE;
                return $this->_success;
            }

            return $this->_fail();
        }

        return ($this->_redis->setTimeout($this->_key_prefix.$session_id, $this->_config['expiration']))
            ? $this->_success
            : $this->_fail();
    }

    public function close()
    {
        if (isset($this->_redis))
        {
            try {
                if ($this->_redis->ping() === '+PONG')
                {
                    $this->_release_lock();
                    if ($this->_redis->close() === FALSE)
                    {
                        return $this->_fail();
                    }
                }
            }
            catch (RedisException $e)
            {
                log_message('error', 'Session: Got RedisException on close(): '.$e->getMessage());
            }

            $this->_redis = NULL;
            return $this->_success;
        }

        return $this->_success;
    }

    public function destroy($session_id)
    {
        if (isset($this->_redis, $this->_lock_key))
        {
            if (($result = $this->_redis->delete($this->_key_prefix.$session_id)) !== 1)
            {
                log_message('debug', 'Session: Redis::delete() expected to return 1, got '.var_export($result, TRUE).' instead.');
            }

            $this->_cookie_destroy();
            return $this->_success;
        }

        return $this->_fail();
    }

    public function gc($maxlifetime)
    {
        return $this->_success;
    }

    protected function _get_lock($session_id)
    {
        if ($this->_lock_key === $this->_key_prefix.$session_id.':lock')
        {
            return $this->_redis->setTimeout($this->_lock_key, 300);
        }

        $lock_key = $this->_key_prefix.$session_id.':lock';
        $attempt = 0;
        do
        {
            if (($ttl = $this->_redis->ttl($lock_key)) > 0)
            {
                sleep(1);
                continue;
            }

            $result = ($ttl === -2)
                ? $this->_redis->set($lock_key, time(), array('nx', 'ex' => 300))
                : $this->_redis->setex($lock_key, 300, time());

            if ( ! $result)
            {
                log_message('error', 'Session: Error while trying to obtain lock for '.$this->_key_prefix.$session_id);
                return FALSE;
            }

            $this->_lock_key = $lock_key;
            break;
        }
        while (++$attempt < 30);

        if ($attempt === 30)
        {
            log_message('error', 'Session: Unable to obtain lock for '.$this->_key_prefix.$session_id.' after 30 attempts, aborting.');
            return FALSE;
        }
        elseif ($ttl === -1)
        {
            log_message('debug', 'Session: Lock for '.$this->_key_prefix.$session_id.' had no TTL, overriding.');
        }

        $this->_lock = TRUE;
        return TRUE;
    }

    protected function _release_lock()
    {
        if (isset($this->_redis, $this->_lock_key) && $this->_lock)
        {
            if ( ! $this->_redis->delete($this->_lock_key))
            {
                log_message('error', 'Session: Error while trying to free lock for '.$this->_lock_key);
                return FALSE;
            }

            $this->_lock_key = NULL;
            $this->_lock = FALSE;
        }

        return TRUE;
    }
}

