class URLChecker {
    const POLL_INTERVAL_MS = 1000;
    const CONNECT_TIMEOUT_MS = 5000;

    private $pollIntervalMs;
    private $connectTimeoutMs;

    public function __construct() {
        $this->pollIntervalMs = self::POLL_INTERVAL_MS;
        $this->connectTimeoutMs = self::CONNECT_TIMEOUT_MS;
    }

    public function waitUntilAvailable(int $timeout_in_ms, string $url): self {
        $start = microtime(true);
        while (true) {
            $responseCode = $this->getHTTPResponseCode($url);
            if ($responseCode === 200) {
                return $this;
            }
            if ((microtime(true) - $start) * 1000 > $timeout_in_ms) {
                throw new Exception("Timeout reached before URL became available");
            }
            usleep($this->pollIntervalMs * 1000);
        }
    }

    public function waitUntilUnavailable(int $timeout_in_ms, string $url): self {
        $start = microtime(true);
        while (true) {
            $responseCode = $this->getHTTPResponseCode($url);
            if ($responseCode !== 200) {
                return $this;
            }
            if ((microtime(true) - $start) * 1000 > $timeout_in_ms) {
                throw new Exception("Timeout reached before URL became unavailable");
            }
            usleep($this->pollIntervalMs * 1000);
        }
    }

    private function getHTTPResponseCode(string $url): ?int {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_TIMEOUT_MS, $this->connectTimeoutMs);
        $response = curl_exec($ch);
        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);
        return $httpCode === false ? null : $httpCode;
    }
