        CURLOPT_RETURNTRANSFER => true,
        CURLOPT_FOLLOWLOCATION => true,
        CURLOPT_TIMEOUT => 30,
        CURLOPT_SSL_VERIFYPEER => true,
        CURLOPT_HTTP_VERSION => CURL_HTTP_VERSION_1_1,
        CURLOPT_MAXREDIRS => 10
    ];

    protected $requestArguments = [];

    protected $requestHeader = [
        'User-Agent' => 'PHP-curl-client/1.0'
    ];

    protected $responseBody = '';
    protected $responseHeader = [];
    protected $responseHttpCode = 0;
    protected $responseClientError = null;
    protected $responseClientInfo = [];
    protected $logger = null;

    public function request($uri, $method = 'GET', array $parameters = [], array $headers = [], bool $multipart = false) {
        $this->requestArguments = [
            'uri' => $uri,
            'method' => $method,
            'parameters' => $parameters,
            'headers' => $headers
        ];

        $curl = curl_init();

        // Set default cURL options
        foreach ($this->curlOptions as $key => $value) {
            curl_setopt($curl, $key, $value);
        }

        // Set the URL
        curl_setopt($curl, CURLOPT_URL, $uri);

        // Set HTTP method
        curl_setopt($curl, CURLOPT_CUSTOMREQUEST, $method);

        // Set headers
        $headers = $this->prepareRequestHeaders($headers);
        curl_setopt($curl, CURLOPT_HTTPHEADER, $headers);

        // Set parameters based on method
        if ($method === 'POST' || $method === 'PUT' || $method === 'PATCH') {
            if ($multipart) {
                $this->setMultipartPostFields($curl, $parameters);
            } else {
                curl_setopt($curl, CURLOPT_POSTFIELDS, http_build_query($parameters));
            }
        }

        // Set logger if provided
        if ($this->logger) {
            $this->logger->logRequest($uri, $method, $parameters, $headers);
        }

        // Execute request
        $this->responseBody = curl_exec($curl);

        // Check for cURL errors
        if ($this->responseBody === false) {
            $this->responseClientError = curl_error($curl);
        } else {
            $this->responseClientError = null;
        }

        // Get response info
        $this->responseHttpCode = curl_getinfo($curl, CURLINFO_HTTP_CODE);
        $this->responseClientInfo = curl_getinfo($curl);

        // Process response headers
        $this->responseHeader = $this->fetchResponseHeader($curl, '');

        // Close cURL session
        curl_close($curl);

        // Log response if logger is provided
        if ($this->logger) {
            $this->logger->logResponse($uri, $method, $this->responseBody, $this->responseHttpCode, $this->responseHeader);
        }

        return $this->responseBody;
    }

    protected function fetchResponseHeader($curl, $header) {
        $headers = [];
        $header_line = '';

        while ($header_line = curl_header($curl)) {
            $headers[] = $header_line;
        }

        return $headers;
    }

    protected function prepareRequestHeaders(array $headers) {
        $preparedHeaders = [];

        foreach ($headers as $name => $value) {
            $preparedHeaders[] = $name . ': ' . $value;
        }

        return $preparedHeaders;
    }

    protected function setMultipartPostFields($curl, $parameters) {
        $multipart = [];

        foreach ($parameters as $key => $value) {
            $multipart[] = [
                'name' => $key,
                'contents' => $value
            ];
        }

        curl_setopt($curl, CURLOPT_POSTFIELDS, $multipart);
    }

    public function getResponse() {
        return [
            'body' => $this->responseBody,
            'headers' => $this->responseHeader,
            'http_code' => $this->responseHttpCode,
            'client_error' => $this->responseClientError,
            'client_info' => $this->responseClientInfo
        ];
