class CurlDownloader {
    private $tempDir;
    private $outputFile;

    public function __construct() {
        $this->tempDir = sys_get_temp_dir();
    }

    public function setTempDir(string $tempDir) : void {
        $this->tempDir = $tempDir;
    }

    public function download(string $url) : bool {
        if (empty($url)) {
            throw new ErrorException("URL cannot be empty");
        }

        $filename = basename($url);
        $this->outputFile = $this->tempDir . '/' . $filename;

        $ch = curl_init();
        if (!$ch) {
            throw new ErrorException("cURL initialization failed");
        }

        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_FILE, fopen($this->outputFile, 'wb'));
        curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);
        curl_setopt($ch, CURLOPT_MAXREDIRS, 10);

        $result = curl_exec($ch);
        if ($result === false) {
            $error = curl_error($ch);
            curl_close($ch);
            throw new ErrorException("cURL error: $error");
        }

        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);

        if ($httpCode >= 400) {
            unlink($this->outputFile);
            return false;
        }

        return true;
    }

    public function getFilename() : string {
        if (empty($this->outputFile)) {
            throw new RuntimeException("Download not completed yet");
        }

        return $this->outputFile;
    }

    public function cleanup() : void {
        if (file_exists($this->outputFile)) {
            unlink($this->outputFile);
        }
    }
}

