    var $curlFH = false;
    var $curlDataWritten = 0;
    var $lastURLError = false;

    static function curlWrite($h, $d) {
        global $timthumb;
        if ($timthumb->curlFH === false) return 0;
        $written = fwrite($timthumb->curlFH, $d);
        $timthumb->curlDataWritten += $written;
        return $written;
    }

    function getURL($url, $tempfile) {
        $this->curlFH = fopen($tempfile, 'wb');
        if (!$this->curlFH) {
            $this->lastURLError = 'Could not open temporary file for writing';
            return false;
        }

        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_FILE, $this->curlFH);
        curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);
        curl_setopt($ch, CURLOPT_TIMEOUT, 10);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, false);
        curl_setopt($ch, CURLOPT_WRITEFUNCTION, array('timthumb', 'curlWrite'));

        $result = @curl_exec($ch);
        if ($result === false) {
            $this->lastURLError = 'cURL error: ' . curl_error($ch);
            curl_close($ch);
            fclose($this->curlFH);
            return false;
        }

        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);

        if ($httpCode >= 400) {
            $this->lastURLError = 'HTTP error: ' . $httpCode;
            fclose($this->curlFH);
            return false;
        }

        if ($this->curlDataWritten == 0) {
            $this->lastURLError = 'No data received from URL';
            fclose($this->curlFH);
            return false;
        }

        fclose($this->curlFH);
        return true;
    }
}

