        $http_code = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);

        if ($http_code >= 400) {
            $this->error[] = ['code' => $http_code, 'message' => $response];
            return [];
        }

        return json_decode($response, true);
    }

