class SpotlightClient {
    public function sendRequest(Request $request, string $url): Response {
        if (!function_exists('curl_init')) {
            return new Response(500, 'cURL extension is not available');
        }

        $ch = curl_init();

        try {
            // Validate request data
            if (empty($request->method) || empty($request->headers) || empty($request->body)) {
                throw new Exception('Invalid request data');
            }

            // Set cURL options
            curl_setopt($ch, CURLOPT_URL, $url);
            curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
            curl_setopt($ch, CURLOPT_HTTPHEADER, $request->headers);
            curl_setopt($ch, CURLOPT_TIMEOUT, 10);
            curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $request->method);

            if ($request->body) {
                curl_setopt($ch, CURLOPT_POSTFIELDS, $request->body);
            }

            // Execute the request
            $responseBody = curl_exec($ch);

            // Check for errors
            if (curl_errno($ch)) {
                return new Response(500, curl_error($ch));
            }

            // Get response info
            $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
            $response = new Response($httpCode, $responseBody);

            return $response;
        } catch (Exception $e) {
            return new Response(500, $e->getMessage());
        } finally {
            // Close cURL session
            curl_close($ch);
        }
    }
}

