class timthumb {
    var $curlFH = false;
    var $curlDataWritten = 0;
    var $lastURLError = false;

    static function curlWrite($h, $d) {
        $written = 0;
        if (is_resource($h) && is_string($d)) {
            $written = fwrite($h, $d);
        }
        return $written;
    }

    function getURL($url, $tempfile) {
        $this->curlFH = fopen($tempfile, 'wb');
        if (!$this->curlFH) {
            $this->lastURLError = 'Could not open temporary file';
            return false;
        }

        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_FILE, $this->curlFH);
        curl_setopt($ch, CURLOPT_HEADER, 0);
        curl_setopt($ch, CURLOPT_FOLLOWLOCATION, 1);
        curl_setopt($ch, CURLOPT_TIMEOUT, 30);
        curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, 0);
        curl_setopt($ch, CURLOPT_WRITEFUNCTION, array('timthumb', 'curlWrite'));

        $result = curl_exec($ch);
        if ($result === false) {
            $this->lastURLError = curl_error($ch);
            curl_close($ch);
            fclose($this->curlFH);
            return false;
        }

        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);

        if ($httpCode >= 400) {
            $this->lastURLError = "HTTP error: $httpCode";
            fclose($this->curlFH);
            return false;
        }

        $this->curlDataWritten = ftell($this->curlFH);
        fclose($this->curlFH);
        return true;
    }
