function sendHttpRequest($url) {
    $ch = curl_init();

    // Check if URL is HTTPS
    $isHttps = strpos($url, 'https://') === 0;

    // Set the URL
    curl_setopt($ch, CURLOPT_URL, $url);

    // Set to return the response as a string instead of outputting it
    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);

    // Set SSL verification based on protocol
    if ($isHttps) {
        // Verify SSL certificate by default
        curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, true);
        curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, 2);
    } else {
        // For HTTP, disable SSL verification (not recommended for production)
        curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false);
        curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, 0);
    }

    // Execute the request
    $response = curl_exec($ch);

    // Check for errors
    if (curl_errno($ch)) {
        return false;
    }

    // Close the cURL session
    curl_close($ch);

    return $response;
}

