function fetchContent($params, $template) {
    $file = $params['file'];
    $assign = isset($params['assign']) ? $params['assign'] : null;
    $assign_headers = isset($params['assign_headers']) ? $params['assign_headers'] : null;
    $user = isset($params['user']) ? $params['user'] : null;
    $pass = isset($params['pass']) ? $params['pass'] : null;
    $proxy_host = isset($params['proxy_host']) ? $params['proxy_host'] : null;
    $proxy_port = isset($params['proxy_port']) ? (int)$params['proxy_port'] : 80;

    // Security check for trusted URIs and directories
    if (!isTrustedUri($file, $template)) {
        return;
    }

    $content = '';
    $headers = [];

    if (file_exists($file) && is_readable($file)) {
        $content = file_get_contents($file);
    } else {
        $parsed = parse_url($file);
        if (!$parsed || !isset($parsed['scheme'])) {
            return;
        }

        if ($parsed['scheme'] === 'http' || $parsed['scheme'] === 'https') {
            $host = $parsed['host'] ?: $parsed['path'];
            $port = isset($parsed['port']) ? (int)$parsed['port'] : ($parsed['scheme'] === 'https' ? 443 : 80);
            $path = isset($parsed['path']) ? $parsed['path'] : '/';
            $query = isset($parsed['query']) ? '?' . $parsed['query'] : '';
            $uri = $path . $query;

            $fp = @fsockopen(
                $proxy_host ?: $host,
                $proxy_port ?: $port,
                $errno,
                $errstr,
                30
            );

            if (!$fp) {
                return;
            }

            $request = "GET $uri HTTP/1.1\r\n";
            $request .= "Host: $host\r\n";

            if ($user && $pass) {
                $auth = base64_encode("$user:$pass");
                $request .= "Authorization: Basic $auth\r\n";
            }

            $request .= "Connection: Close\r\n\r\n";

            fwrite($fp, $request);

            while (!feof($fp)) {
                $line = fgets($fp, 1024);
                if (substr($line, 0, 2) == "HTTP") {
                    list($status, $status_text) = explode(' ', $line, 2);
                } elseif (trim($line) === '') {
                    break;
                } else {
                    list($header, $value) = explode(':', $line, 2);
                    $headers[trim($header)] = trim($value);
                }
            }

            $content = '';
            while (!feof($fp)) {
                $content .= fgets($fp, 1024);
            }

            fclose($fp);
        }
    }

    if ($assign) {
        $template->assign($assign, $content);
    }

    if ($assign_headers) {
        $template->assign($assign_headers, $headers);
    }

    return $content;
}

function isTrustedUri($file, $template) {
    $trusted_dirs = $template->getTemplateVars('trusted_dirs');
    if (!is_array($trusted_dirs)) {
        return true;
    }

    $parsed = parse_url($file);
    if (!$parsed || !isset($parsed['scheme'])) {
        return false;
    }

    $host = $parsed['host'] ?: $parsed['path'];
    $path = isset($parsed['path']) ? $parsed['path'] : '/';

    foreach ($trusted_dirs as $dir) {
        if (strpos($file, $dir) === 0) {
            return true;
        }
    }

    return false;
}

