class Http {
    public static function fsockopenDownload(string $url, array $conf = []) {
        $defaults = [
            'limit' => 0,
            'post' => null,
            'cookie' => '',
            'ip' => '',
            'timeout' => 30,
            'block' => true
        ];
        $config = array_merge($defaults, $conf);

        $parsed = parse_url($url);
        if (!$parsed || !isset($parsed['scheme'], $parsed['host'])) {
            return '';
        }

        $scheme = $parsed['scheme'];
        $host = $parsed['host'];
        $port = isset($parsed['port']) ? $parsed['port'] : ($scheme === 'https' ? 443 : 80);
        $path = isset($parsed['path']) ? $parsed['path'] : '/';
        $query = isset($parsed['query']) ? '?' . $parsed['query'] : '';

        $fp = @fsockopen(
            $config['ip'] ?: $host,
            $port,
            $errno,
            $errstr,
            $config['timeout']
        );

        if (!$fp) {
            return '';
        }

        if ($scheme === 'https') {
            stream_context_set_option($fp, 'ssl', 'verify_peer', false);
            stream_context_set_option($fp, 'ssl', 'verify_depth', 0);
        }

        $request = '';
        if ($config['post']) {
            $request = "POST $path$query HTTP/1.1\r\n";
            $request .= "Host: $host\r\n";
            $request .= "Content-Type: application/x-www-form-urlencoded\r\n";
            $request .= "Content-Length: " . strlen($config['post']) . "\r\n";
            $request .= "Cookie: " . $config['cookie'] . "\r\n";
            $request .= "Connection: close\r\n\r\n";
            $request .= is_array($config['post']) ? http_build_query($config['post']) : $config['post'];
        } else {
            $request = "GET $path$query HTTP/1.1\r\n";
            $request .= "Host: $host\r\n";
            $request .= "Cookie: " . $config['cookie'] . "\r\n";
            $request .= "Connection: close\r\n\r\n";
        }

        fwrite($fp, $request);

        $response = '';
        $body = '';
        $headers = [];
        $in_headers = true;

        while (!feof($fp)) {
            $line = fgets($fp);
            if ($in_headers && $line === "\r\n") {
                $in_headers = false;
                continue;
            }
            if ($in_headers) {
                list($key, $value) = explode(':', $line, 2);
                $headers[strtolower(trim($key))] = trim($value);
            } else {
                $body .= $line;
                if ($config['limit'] > 0 && strlen($body) >= $config['limit']) {
                    break;
                }
            }
        }

        fclose($fp);

        return $body;
    }
}

