            }
            $this->_redis->select((int)$config['database']);
        }
    }

    public function get($key) {
        $key = $this->_get_key($key);
        $value = $this->_redis->get($key);
        if ($value === false) {
            return false;
        }
        if (isset($this->_serialized[$key])) {
            return unserialize($value);
        }
        return $value;
    }

    public function save($id, $data, $ttl = 60, $raw = FALSE) {
        $key = $this->_get_key($id);
        if ($raw) {
            $this->_redis->setex($key, $ttl, $data);
            return true;
        }
        $serialized = serialize($data);
        $this->_redis->setex($key, $ttl, $serialized);
        $this->_serialized[$key] = true;
        return true;
    }

    public function delete($key) {
        $key = $this->_get_key($key);
        return $this->_redis->delete($key);
    }

    public function increment($id, $offset = 1) {
        $key = $this->_get_key($id);
        return $this->_redis->incrby($key, $offset);
    }

    public function decrement($id, $offset = 1) {
        $key = $this->_get_key($id);
        return $this->_redis->decrby($key, $offset);
    }

    public function clean() {
        return $this->_redis->flushDB();
    }

    public function cache_info($type = NULL) {
        return $this->_redis->info($type);
    }

    public function get_metadata($key) {
        $key = $this->_get_key($key);
        $exists = $this->_redis->exists($key);
        if (!$exists) {
            return false;
        }
        $ttl = $this->_redis->ttl($key);
        return array('ttl' => $ttl, 'exists' => $exists);
    }

    public function is_supported() {
        return extension_loaded('redis');
    }

    public function __destruct() {
        if (is_resource($this->_redis)) {
            $this->_redis->close();
        }
    }

    protected function _get_key($key) {
        return $key;
    }

    protected function get_config() {
        $CI =& get_instance();
        return $CI->config->item('cache.redis');
    }
