    public function __construct(string $appid, string $rsaPrivateKey) {
        $this->appId = $appid;
        $this->rsaPrivateKey = $rsaPrivateKey;
    }

    public function doQuery(string $outBizBo = '', string $orderId = '') {
        $params = [
            'app_id' => $this->appId,
            'method' => 'alipay.fund.trans.order.query',
            'format' => 'json',
            'charset' => $this->charset,
            'sign_type' => 'RSA',
            'timestamp' => date('Y-m-d H:i:s'),
            'version' => '1.0',
            'biz_content' => json_encode([
                'out_biz_no' => $outBizBo,
                'order_id' => $orderId
            ])
        ];

        if ($this->checkEmpty($outBizBo) && $this->checkEmpty($orderId)) {
            throw new Exception("Both outBizBo and orderId cannot be empty.");
        }

        $params['sign'] = $this->generateSign($params);
        $response = $this->curlPost('https://api.alipay.com/gateway.do', $params);
        return json_decode($response, true);
    }

    private function generateSign(array $params, string $signType = 'RSA') {
        $signContent = $this->getSignContent($params);
        return $this->sign($signContent, $signType);
    }

    private function sign(string $data, string $signType = 'RSA') {
        $privateKey = "-----BEGIN RSA PRIVATE KEY-----\n" . wordwrap($this->rsaPrivateKey, 64, "\n", true) . "\n-----END RSA PRIVATE KEY-----";
        openssl_sign($data, $signature, $privateKey, OPENSSL_ALGO_SHA256);
        return base64_encode($signature);
    }

    private function checkEmpty(mixed $value) {
        return is_null($value) || $value === '';
    }

    private function getSignContent(array $params) {
        ksort($params);
        $content = [];
        foreach ($params as $key => $val) {
            if ($key !== 'sign' && $key !== 'sign_type') {
                $content[] = $key . '=' . $val;
            }
        }
        return implode('&', $content);
    }

    private function characet(string $data, string $targetCharset) {
        if (function_exists('mb_convert_encoding')) {
            return mb_convert_encoding($data, $targetCharset, 'UTF-8');
        } else {
            return $data;
        }
    }

    private function curlPost(string $url, mixed $postData, array $options = []) {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_POST, 1);
        curl_setopt($ch, CURLOPT_POSTFIELDS, json_encode($postData));
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
        curl_setopt($ch, CURLOPT_HTTPHEADER, ['Content-Type: application/json']);
        curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false);
        curl_setopt($ch, CURLOPT_TIMEOUT, 30);

        foreach ($options as $option => $value) {
            curl_setopt($ch, $option, $value);
        }

        $response = curl_exec($ch);
        curl_close($ch);
        return $response;
    }
}

