class timthumb {
    public $curlFH;
    public $curlDataWritten = 0;
    public $lastURLError = false;

    public static function curlWrite($h, $d) {
        static $written = 0;
        $maxSize = 1024 * 1024 * 5; // 5MB
        if ($written + strlen($d) > $maxSize) {
            return 0;
        }
        $written += strlen($d);
        return fwrite($h, $d);
    }

    public function getURL($url, $tempfile) {
        if (!function_exists('curl_init')) {
            if ($content = @file_get_contents($url)) {
                file_put_contents($tempfile, $content);
                return true;
            } else {
                $this->lastURLError = "Failed to fetch URL using file_get_contents.";
                return false;
            }
        }

        $ch = curl_init();
        $this->curlFH = fopen($tempfile, 'w');
        if (!$this->curlFH) {
            $this->lastURLError = "Failed to open temporary file for writing.";
            return false;
        }

        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, false);
        curl_setopt($ch, CURLOPT_HEADER, false);
        curl_setopt($ch, CURLOPT_FILE, $this->curlFH);
        curl_setopt($ch, CURLOPT_WRITEFUNCTION, array(__CLASS__, 'curlWrite'));

        $response = curl_exec($ch);

        if ($response === false) {
            $this->lastURLError = 'cURL error: ' . curl_error($ch);
            fclose($this->curlFH);
            unlink($tempfile);
            return false;
        }

        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        if ($httpCode >= 400) {
            if ($httpCode === 404) {
                $this->set404();
            } else {
                $this->error("HTTP error: $httpCode");
            }
            fclose($this->curlFH);
            unlink($tempfile);
            return false;
        }

        $this->curlDataWritten = curl_getinfo($ch, CURLINFO_SIZE_DOWNLOAD);
        curl_close($ch);
        return true;
    }

    private function set404() {
        $this->lastURLError = "HTTP 404 - File not found.";
    }

    private function error($message) {
        $this->lastURLError = $message;
    }
}

