class Curl {
    private $curlOptions = [
        CURLOPT_RETURNTRANSFER => true,
        CURLOPT_HEADER => true,
        CURLOPT_FOLLOWLOCATION => true,
        CURLOPT_TIMEOUT => 30,
        CURLOPT_SSL_VERIFYPEER => true,
        CURLOPT_SSL_VERIFYHOST => 2
    ];
    private $requestArguments = [];
    private $requestHeader = [
        'User-Agent' => 'PHP-Curl/1.0'
    ];
    private $responseBody = '';
    private $responseHeader = [];
    private $responseHttpCode = 0;
    private $responseClientError = null;
    private $responseClientInfo = [];
    private $logger = null;

    public function request(string $uri, string $method = 'GET', array $parameters = [], array $headers = [], bool $multipart = false): string {
        $ch = curl_init();

        $this->requestArguments = [
            'uri' => $uri,
            'method' => strtoupper($method),
            'parameters' => $parameters,
            'headers' => $headers
        ];

        curl_setopt_array($ch, $this->curlOptions);

        if ($multipart) {
            curl_setopt($ch, CURLOPT_POST, true);
            curl_setopt($ch, CURLOPT_POSTFIELDS, $parameters);
        } else {
            switch (strtoupper($method)) {
                case 'POST':
                    curl_setopt($ch, CURLOPT_POST, true);
                    curl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($parameters));
                    break;
                case 'PUT':
                    curl_setopt($ch, CURLOPT_CUSTOMREQUEST, 'PUT');
                    curl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($parameters));
                    break;
                case 'DELETE':
                    curl_setopt($ch, CURLOPT_CUSTOMREQUEST, 'DELETE');
                    break;
                case 'PATCH':
                    curl_setopt($ch, CURLOPT_CUSTOMREQUEST, 'PATCH');
                    curl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($parameters));
                    break;
                default:
                    curl_setopt($ch, CURLOPT_HTTPGET, true);
            }
        }

        $headers = $this->prepareRequestHeaders();
        curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);

        curl_setopt($ch, CURLOPT_URL, $uri);

        if ($this->logger) {
            $this->logger->log('Request: ' . $method . ' ' . $uri . PHP_EOL . print_r($headers, true) . PHP_EOL . print_r($parameters, true));
        }

        $response = curl_exec($ch);

        if ($response === false) {
            $this->responseClientError = curl_error($ch);
        }

        $this->responseClientInfo = curl_getinfo($ch);

        $headerSize = $this->responseClientInfo['header_size'];
        $this->responseHeader = explode("\r\n", substr($response, 0, $headerSize));
        $this->responseBody = substr($response, $headerSize);

        $this->responseHttpCode = $this->responseClientInfo['http_code'];

        curl_close($ch);

        if ($this->logger) {
            $this->logger->log('Response: HTTP/' . $this->responseClientInfo['http_version'] . ' ' . $this->responseHttpCode . PHP_EOL . print_r($this->responseHeader, true) . PHP_EOL . $this->responseBody);
        }

        return $this->responseBody;
    }

    public function getResponse(): array {
        return [
            'requestArguments' => $this->requestArguments,
            'responseBody' => $this->responseBody,
            'responseHeader' => $this->responseHeader,
            'responseHttpCode' => $this->responseHttpCode,
            'responseClientError' => $this->responseClientError,
            'responseClientInfo' => $this->responseClientInfo
        ];
    }

    public function setCurlOptions(array $curlOptions): void {
        $this->curlOptions = array_merge($this->curlOptions, $curlOptions);
    }

    public function setLogger(object $logger): void {
        $this->logger = $logger;
    }

    public function getResponseBody(): string {
        return $this->responseBody;
    }

    public function getResponseHeader(): array {
        return $this->responseHeader;
    }

    public function getResponseHttpCode(): int {
        return $this->responseHttpCode;
    }

    public function getResponseClientError(): ?string {
        return $this->responseClientError;
    }

    public function fetchResponseHeader(resource $curl, string $header): int {
        $this->responseHeader[] = $header;
        return strlen($header);
    }

    public function prepareRequestHeaders(): array {
        $headers = [];

        foreach ($this->requestHeader as $name => $value) {
            $headers[] = "$name: $value";
        }

        foreach ($this->requestArguments['headers'] as $name => $value) {
            $headers[] = "$name: $value";
        }

