class Client {
    public static function get(string $url, array $headers = []) : Response {
        return self::sendRequest(new Request($url, 'GET', null, $headers));
    }

    public static function delete(string $url, array $headers = []) : Response {
        return self::sendRequest(new Request($url, 'DELETE', null, $headers));
    }

    public static function post(string $url, mixed $body, array $headers = []) : Response {
        return self::sendRequest(new Request($url, 'POST', $body, $headers));
    }

    public static function multipartPost(string $url, array $fields, string $name, string $fileName, string $fileBody, string $mimeType = null, array $headers = []) : Response {
        $boundary = '----' . md5(mt_rand() . microtime());
        $body = '';

        foreach ($fields as $fieldName => $fieldValue) {
            $body .= '--' . $boundary . "\r\n";
            $body .= 'Content-Disposition: form-data; name="' . self::escapeQuotes($fieldName) . '"\r\n\r\n';
            $body .= $fieldValue . "\r\n";
        }

        $body .= '--' . $boundary . "\r\n";
        $body .= 'Content-Disposition: form-data; name="' . $name . '"'; 
        if ($mimeType) {
            $body .= '; filename="' . $fileName . '"\r\n';
            $body .= 'Content-Type: ' . $mimeType . "\r\n";
        } else {
            $body .= '; filename="' . $fileName . '"\r\n';
            $body .= 'Content-Type: application/octet-stream\r\n';
        }
        $body .= "\r\n";
        $body .= $fileBody . "\r\n";
        $body .= '--' . $boundary . '--';

        $headers['Content-Type'] = 'multipart/form-data; boundary=' . $boundary;
        return self::sendRequest(new Request($url, 'POST', $body, $headers));
    }

    public static function sendRequest(Request $request) : Response {
        $ch = curl_init();

        curl_setopt_array($ch, [
            CURLOPT_URL => $request->getUrl(),
            CURLOPT_RETURNTRANSFER => true,
            CURLOPT_HEADER => true,
            CURLOPT_FOLLOWLOCATION => true,
            CURLOPT_HTTP_VERSION => CURL_HTTP_VERSION_1_1,
            CURLOPT_CUSTOMREQUEST => $request->getMethod(),
            CURLOPT_POSTFIELDS => $request->getBody(),
            CURLOPT_HTTPHEADER => $request->getHeaders()
        ]);

        $response = curl_exec($ch);
        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);

        if ($response === false) {
            $error = curl_error($ch);
            curl_close($ch);
            throw new Exception("cURL error: $error");
        }

        $headerSize = curl_getinfo($ch, CURLINFO_HEADER_SIZE);
        $rawHeaders = substr($response, 0, $headerSize);
        $body = substr($response, $headerSize);

        $headers = self::parseHeaders($rawHeaders);
        return new Response($httpCode, $headers, $body);
    }

    public static function userAgent() : string {
        return 'PHP-SDK/' . phpversion() . ' (' . $_SERVER['HTTP_USER_AGENT'] . ')';
    }

    public static function parseHeaders(string $raw) : array {
        $headers = [];
        $lines = explode("\r\n", $raw);
        foreach ($lines as $line) {
            if (strpos($line, ':') !== false) {
                list($name, $value) = explode(':', $line, 2);
                $headers[strtolower(trim($name))] = trim($value);
            }
        }
        return $headers;
    }

    public static function escapeQuotes(string $str) : string {
        return str_replace(["\"", "'"], ['\"', "'"], $str);
    }

    public static function ucwordsHyphen(string $str) : string {
        return implode('-', array_map('ucwords', explode('-', $str)));
    }
}

class Request {
    private $url;
    private $method;
    private $body;
    private $headers;

    public function __construct(string $url, string $method, mixed $body = null, array $headers = []) {
        $this->url = $url;
        $this->method = $method;
        $this->body = $body;
        $this->headers = $headers;
    }

    public function getUrl() : string {
        return $this->url;
    }

    public function getMethod() : string {
        return $this->method;
    }

    public function getBody() : string {
        return $this->body;
    }

    public function getHeaders() : array {
