        ksort($params);
        $requestParams = array();
        foreach ($params as $key => $value) {
            $requestParams[] = rawurlencode($key) . '=' . rawurlencode($value);
        }
        return $method.'&'.rawurlencode($baseURI).'&'.rawurlencode(implode('&', $requestParams));
    }

