        $paramString = array();
        foreach ($oauth as $key => $value) {
            $paramString[] = $key.'="'.rawurlencode($value).'"';
        }
        $authorizationHeader .= implode(', ', $paramString);
        return $authorizationHeader;
    }

    public function performRequest(bool $return = true) {
