        string $fileName,
        string $fileBody,
        string $mimeType = null,
        array $headers = []
    ) : Response {
        $boundary = uniqid('------------------');
        $request = new Request($url, 'POST', null, $headers);
        $request->setHeader('Content-Type', 'multipart/form-data; boundary=' . $boundary);

        $body = '';
        foreach ($fields as $key => $value) {
            $body .= "--" . $boundary . "\r\n";
            $body .= "Content-Disposition: form-data; name=\"" . self::escapeQuotes($key) . "\"\r\n\r\n";
