    }

    public static function parseHeaders(string $raw) : array {
        $headers = [];
        $lines = explode("\r\n", $raw);
        foreach ($lines as $line) {
            if (trim($line)) {
                list($name, $value) = explode(':', $line, 2);
                $headers[self::ucwordsHyphen(trim($name))] = trim($value);
            }
        }
        return $headers;
    }

    public static function escapeQuotes(string $str) : string {
        return str_replace(['"', "'"], ['\"', "'"], $str);
    }

    public static function ucwordsHyphen(string $str) : string {
