class Downloader {
    public static function start(string|array $url, string $saveFile, int $timeout = 10): array {
        if (!is_array($url)) {
            $url = [$url];
        }

        $results = [];
        foreach ($url as $u) {
            $result = self::downloadFile($u, $saveFile, $timeout);
            $results[] = $result;
        }
        return $results;
    }

    private static function downloadFile(string $url, string $saveFile, int $timeout): array {
        $headers = self::getHeaders($url);
        $responseCode = $headers['response_code'] ?? 0;
        $contentType = $headers['content_type'] ?? '';
        $contentLength = $headers['content_length'] ?? 0;

        if ($responseCode !== 200) {
            return ['error' => "HTTP error: $responseCode", 'file' => $saveFile];
        }

        if (self::isGzip($saveFile)) {
            self::checkGzip($saveFile);
        }

        if (self::supportsRange($contentType)) {
            $result = self::fileDownloadCurl($url, $saveFile, true, 0, $contentLength);
        } else {
            $result = self::fileDownloadFopen($url, $saveFile);
        }

        return $result;
    }

    private static function getHeaders(string $url): array {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_HEADER, true);
        curl_setopt($ch, CURLOPT_NOBODY, true);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);
        curl_setopt($ch, CURLOPT_TIMEOUT, 10);
        $response = curl_exec($ch);
        $headerSize = curl_getinfo($ch, CURLINFO_HEADER_SIZE);
        $headers = substr($response, 0, $headerSize);
        $body = substr($response, $headerSize);
        curl_close($ch);

        $headersArray = [];
        $lines = explode("\r\n", $headers);
        foreach ($lines as $line) {
            if (strpos($line, ':') === false) continue;
            list($key, $value) = explode(':', $line, 2);
            $headersArray[strtolower(trim($key))] = trim($value);
        }

        $headersArray['response_code'] = curl_errno($ch) ? curl_errno($ch) : curl_getinfo($ch, CURLINFO_HTTP_CODE);
        return $headersArray;
    }

    private static function supportsRange(string $contentType): bool {
        return strpos($contentType, 'application/octet-stream') !== false ||
               strpos($contentType, 'application/pdf') !== false ||
               strpos($contentType, 'video/') !== false ||
               strpos($contentType, 'image/') !== false;
    }

    private static function fileDownloadFopen(string $url, string $fileName): array {
        $fp = fopen($url, 'rb');
        if (!$fp) {
            return ['error' => "Failed to open URL: $url", 'file' => $fileName];
        }

        $fpOut = fopen($fileName, 'wb');
        if (!$fpOut) {
            fclose($fp);
            return ['error' => "Failed to create file: $fileName", 'file' => $fileName];
        }

        while (!feof($fp)) {
            $data = fread($fp, 8192);
            if (fwrite($fpOut, $data) === false) {
                fclose($fp);
                fclose($fpOut);
                return ['error' => "Failed to write to file: $fileName", 'file' => $fileName];
            }
        }

        fclose($fp);
        fclose($fpOut);
        return ['success' => true, 'file' => $fileName];
    }

    private static function fileDownloadCurl(string $url, string $fileName, bool $supportRange, int $existsLength, int $length): array {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);
        curl_setopt($ch, CURLOPT_TIMEOUT, 10);
        curl_setopt($ch, CURLOPT_HEADER, true);
        curl_setopt($ch, CURLOPT_WRITEHEADER, true);

        if ($supportRange && $existsLength > 0) {
            $rangeStart = $existsLength;
            $rangeEnd = $length - 1;
            curl_setopt($ch, CURLOPT_RANGE, "$rangeStart-$rangeEnd");
        }

        $response = curl_exec($ch);
        if (curl_errno($ch)) {
            curl_close($ch);
            return ['error' => "cURL error: " . curl_error($ch), 'file' => $fileName];
        }

        $headerSize = curl_getinfo($ch, CURLINFO_HEADER_SIZE);
        $headers = substr($response, 0, $headerSize);
        $body = substr($response, $headerSize);

        $fpOut = fopen($fileName, 'ab');
        if (!$fpOut) {
            curl_close($ch);
            return ['error' => "Failed to create file: $fileName", 'file' => $fileName];
        }

        if (fwrite($fpOut, $body) === false) {
            fclose($fpOut);
            curl_close($ch);
            return ['error' => "Failed to write to file: $fileName", 'file' => $fileName];
        }

        fclose($fpOut);
        curl_close($ch);
        return ['success' => true, 'file' => $fileName];
    }

    private static function isGzip(string $file): bool {
        $finfo = finfo_open(FILEINFO_MIME_TYPE);
        $mimeType = finfo_file($finfo, $file);
        finfo_close($finfo);
        return strpos($mimeType, 'gzip') !== false;
    }

    private static function checkGzip(string $file): void {
        $finfo = finfo_open(FILEINFO_MIME_TYPE);
        $mimeType = finfo_file($finfo, $file);
        finfo_close($finfo);

        if (strpos($mimeType, 'gzip') === false) {
            return;
        }

        $gzFile = gzopen($file, 'rb');
        if (!$gzFile) {
            return;
        }

        $content = '';
        while (!gzeof($gzFile)) {
            $content .= gzread($gzFile, 8192);
        }
        gzclose($gzFile);

        file_put_contents($file . '.uncompressed', $content);
        unlink($file);
        rename($file . '.uncompressed', $file);
    }
}
