                    $this->config[$key] = $value;
                }
            }
        }
        $this->bcs = new BaiduBCS($this->config['AccessKey'], $this->config['SecretKey']);
    }

    public function request(string $path, string $method, array $headers = [], $body = null) {
        $url = $this->config['bucket'] . '/' . $this->rootPath . $path;
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_HEADER, true);
        curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $method);
        curl_setopt($ch, CURLOPT_TIMEOUT, $this->config['timeout']);
        curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);
        if ($body !== null) {
            curl_setopt($ch, CURLOPT_POSTFIELDS, $body);
        }

        $response = curl_exec($ch);
        if ($response === false) {
            $this->error = 'cURL error: ' . curl_error($ch);
            curl_close($ch);
            return false;
        }

        $headerSize = curl_getinfo($ch, CURLINFO_HEADER_SIZE);
        $headerText = substr($response, 0, $headerSize);
        $bodyText = substr($response, $headerSize);

        if (strpos($headerText, 'HTTP/1.1 200 OK') === false) {
            $this->error = 'HTTP error: ' . $headerText;
            curl_close($ch);
            return false;
        }

        $this->error = '';
        curl_close($ch);
        return $this->response($bodyText);
    }

    public function response(string $text) {
        $json = json_decode($text, true);
        if (json_last_error() === JSON_ERROR_NONE) {
            return $json;
        } else {
            return ['error' => 'Invalid JSON response'];
        }
    }

    public function error(string $header) {
        $this->error = $header;
    }

    public function uploadFile(string $filePath, string $remotePath) {
        $file = fopen($filePath, 'rb');
        if (!$file) {
            $this->error = 'Failed to open file for upload';
            return false;
        }

        $headers = [
            'Content-Type: application/octet-stream',
            'Content-Length: ' . filesize($filePath)
        ];

        $result = $this->request($remotePath, 'PUT', $headers, fread($file, filesize($filePath)));
        fclose($file);
        return $result;
    }

    public function downloadFile(string $remotePath, string $localPath) {
        $result = $this->request($remotePath, 'GET');
        if ($result && isset($result['Body'])) {
            file_put_contents($localPath, $result['Body']);
            return true;
        }
        return false;
    }

    public function listFiles(string $prefix = '') {
        $params = ['prefix' => $prefix];
        $result = $this->request($prefix, 'GET', ['query' => http_build_query($params)]);
        return $result;
    }
}

