        curl_setopt($this->curl, CURLOPT_SSL_VERIFYPEER, true);
        curl_setopt($this->curl, CURLOPT_SSL_VERIFYHOST, 2);
        if ($this->proxy) {
            curl_setopt($this->curl, CURLOPT_PROXY, $this->proxy);
        }
        if ($this->keepAlive) {
            curl_setopt($this->curl, CURLOPT_HTTPHEADER, ['Connection: keep-alive']);
            curl_setopt($this->curl, CURLOPT_TCP_KEEPALIVE, true);
            curl_setopt($this->curl, CURLOPT_TCP_KEEPIDLE, $this->keepAliveTimeout);
            curl_setopt($this->curl, CURLOPT_TCP_KEEPINTVL, 60);
        } else {
            curl_setopt($this->curl, CURLOPT_HTTPHEADER, ['Connection: close']);
        }
    }

