class File implements SimplePie\HTTP\Response {
    public string $url;
    public string $useragent;
    public bool $success;
    public array $headers;
    public string $body;
    public int $status_code;
    public int $redirects;
    public string $error;
    public int $method;
    public string $permanent_url;
    public bool $permanentUrlMutable;

    public function __construct(string $url, int $timeout = 10, int $redirects = 5, ?array $headers = null, ?string $useragent = null, bool $force_fsockopen = false, array $curl_options = []) {
        $this->permanent_url = $url;
        $this->permanentUrlMutable = true;
        $this->redirects = 0;
        $this->error = '';
        $this->method = 0;
        $this->headers = [];
        $this->body = '';
        $this->status_code = 0;
        $this->success = false;

        $ch = curl_init();

        if ($useragent === null) {
            $useragent = 'Mozilla/5.0 (compatible; PHP-CURL)';
        }

        curl_setopt($ch, CURLOPT_USERAGENT, $useragent);
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);
        curl_setopt($ch, CURLOPT_MAXREDIRS, $redirects);
        curl_setopt($ch, CURLOPT_TIMEOUT, $timeout);
        curl_setopt($ch, CURLOPT_HEADER, true);
        curl_setopt($ch, CURLOPT_HTTPHEADER, $headers ?? []);
        curl_setopt($ch, CURLOPT_FRESH_CONNECT, true);
        curl_setopt($ch, CURLOPT_FORBID_REUSE, true);

        if ($force_fsockopen) {
            curl_setopt($ch, CURLOPT_USE_SSL, CURLUSESSL_TRY);
        }

        foreach ($curl_options as $option => $value) {
            curl_setopt($ch, $option, $value);
        }

        $response = curl_exec($ch);

        if ($response === false) {
            $this->error = curl_error($ch);
            $this->success = false;
        } else {
            $this->success = true;
        }

        $this->headers = $this->parse_headers($response);
        $this->body = substr($response, strlen(implode("\r\n", $this->headers)) + 2);
        $this->status_code = intval(substr($this->headers[0], 9, 3));
        $this->url = curl_getinfo($ch, CURLINFO_EFFECTIVE_URL);
        $this->redirects = curl_getinfo($ch, CURLINFO_REDIRECT_COUNT);

        curl_close($ch);
    }

    private function parse_headers($header_str) {
        $headers = [];
        $lines = explode("\r\n", $header_str);
        $i = 0;
        while ($i < count($lines)) {
            $line = $lines[$i];
            if (strpos($line, ':') !== false) {
                list($key, $value) = explode(':', $line, 2);
                $headers[strtolower(trim($key))] = trim($value);
            }
            $i++;
        }
        return $headers;
    }

    public function get_body_content(): string {
        return $this->body;
    }

    public function get_headers(): array {
        return $this->headers;
    }

    public function get_status_code(): int {
        return $this->status_code;
    }

    public function get_final_requested_uri(): string {
        return $this->url;
    }

    public function get_permanent_uri(): string {
        return $this->permanent_url;
    }
}

