class timthumb {
    public $curlFH;
    public $curlDataWritten = 0;
    public $lastURLError;

    public static function curlWrite($h, $d) {
        $obj = new timthumb();
        $bytes = strlen($d);
        if ($obj->curlDataWritten + $bytes > 1048576) { // 1MB limit
            return 0;
        }
        $obj->curlDataWritten += $bytes;
        fwrite($obj->curlFH, $d);
        return $bytes;
    }

    public function getURL($url, $tempfile) {
        $this->curlFH = fopen($tempfile, 'w');
        if (!$this->curlFH) {
            $this->lastURLError = "Failed to open temporary file";
            return false;
        }

        $ch = curl_init();
        if (!$ch) {
            $this->lastURLError = "cURL initialization failed";
            fclose($this->curlFH);
            return false;
        }

        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, false);
        curl_setopt($ch, CURLOPT_WRITEFUNCTION, [$this, 'curlWrite']);
        curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);
        curl_setopt($ch, CURLOPT_TIMEOUT, 30);

        try {
            $result = curl_exec($ch);
            if ($result === false) {
                $this->lastURLError = curl_error($ch);
                fclose($this->curlFH);
                curl_close($ch);
                return false;
            }

            $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
            if ($httpCode >= 400) {
                $this->lastURLError = "HTTP error: " . $httpCode;
                fclose($this->curlFH);
                curl_close($ch);
                return false;
            }

            fclose($this->curlFH);
            curl_close($ch);
            return true;
        } catch (Exception $e) {
            $this->lastURLError = $e->getMessage();
            fclose($this->curlFH);
            curl_close($ch);
            return false;
        }
    }
