class GoogleTranslate {
    public static function translate(string $source, string $target, string $text): string {
        if (strlen($text) > 9000) {
            throw new Exception("Input text exceeds the maximum allowed length of 9000 characters.");
        }
        return self::getSentencesFromJSON(self::requestTranslation($source, $target, $text));
    }

    public static function requestTranslation(string $source, string $target, string $text): string {
        $url = 'https://translate.googleapis.com/translate_a/single';
        $data = [
            'client' => 'gtx',
            'sl' => $source,
            'tl' => $target,
            'q' => $text
        ];

        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_POST, 1);
        curl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($data));
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false);

        $response = curl_exec($ch);
        curl_close($ch);

        return $response;
    }

    public static function getSentencesFromJSON(string $json): string {
        $data = json_decode($json, true);
        if (isset($data[0])) {
            $translatedText = '';
            foreach ($data[0] as $sentence) {
                $translatedText .= $sentence[0] . ' ';
            }
            return trim($translatedText);
        }
        return '';
    }
