import logging
import threading
import requests

class SimpleCrawler:
    _results = {}
    MAX_FILE_SIZE = 20 * 1024 * 1024
    MIN_FILE_SIZE = 10
    LOGGER = logging.getLogger(__name__)
    USER_AGENT = 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/58.0.3029.110 Safari/537.3'
    HEADERS = {'User-Agent': USER_AGENT}

    def fetch_url(self, url: str, request_args: dict = None) -> str:
        return self._fetch_url(url, False, request_args)

    def _fetch_url(self, url: str, is_threaded: bool, request_args: dict = None) -> str:
        try:
            if request_args is None:
                request_args = {}
            request_args['headers'] = {**self.HEADERS, **request_args.get('headers', {})}
            response = requests.get(url, **request_args)
            if response.status_code == 200:
                content = response.content
                if self.MIN_FILE_SIZE <= len(content) <= self.MAX_FILE_SIZE:
                    if is_threaded:
                        self._results[url] = content
                    return content
                else:
                    self.LOGGER.warning(f'Invalid file size for {url}')
            else:
                self.LOGGER.warning(f'Failed to fetch {url} with status code {response.status_code}')
        except Exception as e:
            self.LOGGER.error(f'Error fetching {url}: {e}')
        return None

    def fetch_urls(self, urls: list, request_args: dict = None) -> dict:
        threads = []
        for url in urls:
            thread = threading.Thread(target=self._fetch_url, args=(url, True, request_args))
            thread.start()
            threads.append(thread)
        for thread in threads:
            thread.join()
        return self._results
