    def encode_image_to_base64(raw_image_data: Image.Image) -> str:
        buffered = BytesIO()
        raw_image_data.save(buffered, format="PNG")
        return base64.b64encode(buffered.getvalue()).decode('utf-8')

    def generate(self, text_prompt: str, raw_image_data: Union[Image.Image, dict, str]) -> str:
        image_data = raw_image_data
        if isinstance(raw_image_data, Image.Image):
            image_data = self.encode_image_to_base64(raw_image_data)
        elif isinstance(raw_image_data, dict):
            image_data = raw_image_data.get('image_data', '')
        elif isinstance(raw_image_data, str):
            image_data = raw_image_data

        data = {
            "prompt": text_prompt,
            "image_data": image_data,
            "max_new_tokens": self.max_new_tokens
        }

        response = requests.post('https://api.openai.com/v1/engines/davinci-codex/completions', headers=self.headers, json=data)
        if response.status_code == 200:
            return response.json().get('choices', [{}])[0].get('text', '')
        else:
            raise Exception(f"API request failed with status code {response.status_code}: {response.text}")

    @staticmethod
    def _get_pil_image(raw_image_data: Union[Image.Image, dict, str]) -> Image.Image:
        if isinstance(raw_image_data, Image.Image):
            return raw_image_data
        elif isinstance(raw_image_data, dict):
            image_data = raw_image_data.get('image_data', '')
            if image_data.startswith('data:image'):
                _, format, imgstr = image_data.split(';base64,')
                return Image.open(BytesIO(base64.b64decode(imgstr)))
            else:
                return Image.open(BytesIO(base64.b64decode(image_data)))
        elif isinstance(raw_image_data, str):
            if raw_image_data.startswith('data:image'):
                _, format, imgstr = raw_image_data.split(';base64,')
                return Image.open(BytesIO(base64.b64decode(imgstr)))
            else:
                return Image.open(BytesIO(base64.b64decode(raw_image_data)))
        else:
            raise ValueError("Unsupported image format")
