from urllib3.connection import HTTPConnectionPool, Timeout, Retry

class HTTPConnectionPool(HTTPConnectionPool):
    def __init__(self, host, port=None, strict=False, timeout=Timeout.DEFAULT_TIMEOUT, maxsize=1, block=False, headers=None, retries=None, _proxy=None, _proxy_headers=None, **conn_kw):
        super().__init__(host, port, strict, timeout, maxsize, block, headers, retries, _proxy, _proxy_headers, **conn_kw)
        self.pool = queue.LifoQueue(maxsize)
        self.conn_kw = conn_kw

    def _new_conn(self):
        return HTTPConnection(self.host, self.port, **self.conn_kw)

    def _get_conn(self, timeout=None):
        if self.pool.empty():
            conn = self._new_conn()
        else:
            conn = self.pool.get(timeout=timeout)
        return conn

    def _put_conn(self, conn):
        if self.pool.full():
            conn.close()
        else:
            self.pool.put(conn)

    def _make_request(self, conn, method, url, timeout=_Default, **httplib_request_kw):
        conn.request(method, url, timeout=timeout, **httplib_request_kw)
        return conn.getresponse()

    def urlopen(self, method, url, body=None, headers=None, retries=None, redirect=True, assert_same_host=True, timeout=_Default, pool_timeout=None, release_conn=None, **response_kw):
        if retries is None:
            retries = self.retries
        if timeout is _Default:
            timeout = self.timeout
        if pool_timeout is _Default:
            pool_timeout = self.timeout.connect_timeout
        conn = self._get_conn(timeout=pool_timeout)
        try:
            response = self._make_request(conn, method, url, timeout=timeout, **response_kw)
        except:
            conn.close()
            raise
        if release_conn is None:
            release_conn = self.block
        if release_conn:
            self._put_conn(conn)
        else:
            self._close_conn(conn)
        return response
