class FileOnlineDelete_gitee:
    def __init__(self):
        self.url_prefix = "https://gitee.com/api/v5/repos"
        self.show_help = ""

    def delete_file_from_branch(self, repo_owner: str, repo_name: str, file_path: str, branch: str, access_token: str, commit_message: str = "[comfyui_LLM_party] file deleted") -> tuple:
        import requests
        import json

        headers = {
            "Authorization": f"token {access_token}",
            "Content-Type": "application/json"
        }

        file_info_url = f"{self.url_prefix}/{repo_owner}/{repo_name}/contents/{file_path}?ref={branch}"
        response = requests.get(file_info_url, headers=headers)

        if response.status_code != 200:
            return (f"Error retrieving file info: {response.text}",)

        file_info = json.loads(response.text)
        sha = file_info["sha"]

        delete_url = f"{self.url_prefix}/{repo_owner}/{repo_name}/contents/{file_path}"
        delete_payload = {
            "sha": sha,
            "branch": branch,
            "message": commit_message,
            "committer": {
                "name": "comfyui_LLM_party",
                "email": "comfyui_LLM_party@example.com"
            }
        }

        delete_response = requests.delete(delete_url, headers=headers, data=json.dumps(delete_payload))

        if delete_response.status_code != 200:
            return (f"Error deleting file: {delete_response.text}",)

        return ("Success",)
